query ($startTime: DateTime!, $endTime: DateTime!, $localTimeFixedPoint: DateTime!) {
  classificationQuery {
    sourcesClassifiedOverTimeData(timeRequestOptions: {startTime: $startTime, endTime: $endTime}, localTimeFixedPoint: $localTimeFixedPoint) {
      values {
        name
        counts
        __typename
      }
      timeBuckets
      __typename
    }
    __typename
  }
}
